import React, { useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import ReCAPTCHA from 'react-google-recaptcha';
import { Modal, Container, Form, Button, Image, Stack } from 'react-bootstrap';
import meloMixLogo from './meloMixLogo.png';
import './SignUp.css';

const SignUpModal = () => {
  const navigate = useNavigate();
  const { userType } = useParams();
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    username: '',
    email: '',
    password: '',
    password_confirmation: '',
    profile_picture: null
  });
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [captchaToken, setCaptchaToken] = useState('');
  const [show, setShow] = useState(true);

  const handleClose = () => setShow(false);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = e => {
    setFormData({ ...formData, profile_picture: e.target.files[0] });
  };

  const handleCancel = () => {
    navigate('/login');
    setShow(false);
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!captchaToken) {
      setMessage('Please complete the CAPTCHA.');
      return;
    }
    setIsLoading(true);
    try {
      const data = new FormData();
      for (let key in formData) {
        data.append(key, formData[key]);
      }
      const response = await axios.post(`http://localhost:8000/api/register/${userType}`, data);
      setMessage('Registration successful!');
    } catch (error) {
      if (error.response && error.response.status === 422) {
        const validationErrors = error.response.data.errors;
        console.log(validationErrors);
      } else {
        setMessage('Registration failed. Please try again.');
        console.error('Registration failed:', error);
      }
    } finally {
      setIsLoading(false);
      setFormData({
        first_name: '',
        last_name: '',
        username: '',
        email: '',
        password: '',
        password_confirmation: '',
        profile_picture: null
      });
    }
  };

  const handleCaptchaChange = token => {
    setCaptchaToken(token);
  };

  return (
    <Modal show={show} aria-labelledby="contained-modal-title-vcenter"
    centered>
      <Modal.Body>
        <Container>
          {message && <p>{message}</p>}
          <Form onSubmit={handleSubmit} className="d-flex flex-column align-items-center">
          <Stack gap={2}>
          <div className="file-input-container">
              <label htmlFor="profile_picture" className="upload-button">
                {formData.profile_picture ? (
                  <Image src={URL.createObjectURL(formData.profile_picture)} alt="Profile" className="profile-image" />
                ) : (
                  <span>Upload Image</span>
                )}
              </label>
              <Form.Control type="file" name="profile_picture" id="profile_picture" onChange={handleFileChange} />
            </div>
            <Form.Control type="text" name="first_name" placeholder="First Name" value={formData.first_name} onChange={handleChange}  />
            <Form.Control type="text" name="last_name" placeholder="Last Name" value={formData.last_name} onChange={handleChange} />
            <Form.Control type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} />
            <Form.Control type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} />
            <Form.Control type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} />
            <Form.Control type="password" name="password_confirmation" placeholder="Confirm Password" value={formData.password_confirmation} onChange={handleChange} />
            </Stack>
            <Stack gap={3}> 
            <ReCAPTCHA
              sitekey="6Ldldm4pAAAAAKY_GR9PwuY1FG07D3hRNiwG1o8i"
              onChange={handleCaptchaChange}
            />
            </Stack>
            <div className="d-grid gap-2" >
              <Button variant="danger" size="sm" type="submit" disabled={isLoading}>
                SIGN UP
              </Button>
              <Button variant="secondary" size="sm" onClick={handleCancel}>
                CANCEL
              </Button>
            </div>
          </Form>
        </Container>
      </Modal.Body>
    </Modal>
  );
};

export default SignUpModal;
