import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Form, Button, Container, Row, Col, Image, Link } from 'react-bootstrap';
import axios from 'axios';

// Import your images
import meloMixLogo from './meloMixLogo.png';
import loginImage from './loginImage.png';

const Login = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });

  const navigate = useNavigate();

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      console.log('Form data:', formData);
      const response = await axios.post('http://localhost:8000/api/login', formData);
      console.log('Login response:', response.data);
      const { token } = response.data;
      localStorage.setItem('token', token);
      navigate('/user-type-selection');
    } catch (error) {
      console.error('Login failed:', error);
      if (error.response) {
        console.log('Error response:', error.response);
        if (error.response.status === 401) {
          // Unauthorized: incorrect username or password
          alert('Incorrect username or password. Please try again.');
        } else {
          // Other server errors
          alert(`An error occurred during login: ${error.response.data.message}. Please try again later.`);
        }
      } else {
        // Network error or other unexpected errors
        alert(`An unexpected error occurred during login: ${error.message}. Please try again later.`);
      }
    }
  };

  const handleSignUpClick = () => {
    navigate('/user-type-selection');
  };

  return (
  <Container fluid className='font' style={{ backgroundColor: '#800000', minHeight: '100vh', padding:'0px', margin: '0px', position:'relative'}}>
    <Link to="/login" className="d-flex flex-row align-items-center justify-content-start p-3 m-0" style={{ textDecoration: 'none', color: 'inherit' }}>
        <Image src={meloMixLogo} alt="MeloMix Logo" style={{ height: '35px', marginRight: '10px' }}/>
        <h1 style={{ color: 'white', fontSize: '20px', fontWeight:'600', margin: 0 }}>MeloMix</h1>
      </Link>
  <Row className="justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
    <Col xs={12} md={6} className="bg-light p-5 rounded">
      <div className="text-center">
        <Image src={meloMixLogo} alt="MeloMix Logo" fluid style={{ maxWidth: '3%', maxHeight: '3%', marginTop: '15px', marginLeft: '20px' }} />
        <h1 className="text-center mt-4 text-white" style={{ fontSize: '24px', fontFamily: 'Arial, sans-serif' }}>MeloMix</h1>
      </div>
      <Form onSubmit={handleSubmit} className="mt-4">
        <h2 className="text-center text-white mb-4">LOG IN</h2>
        <Form.Group controlId="formBasicUsername">
          <Form.Label className="text-white">Username:</Form.Label>
          <Form.Control type="username" name="username" onChange={handleChange} style={{ backgroundColor: 'white', border: 'none', width: '335px', height: '40px', marginLeft: '7px', borderRadius: '5px' }} />
        </Form.Group>

        <Form.Group controlId="formBasicPassword" style={{ marginTop: '20px' }}>
          <Form.Label className="text-white">Password:</Form.Label>
          <Form.Control type="password" name="password" onChange={handleChange} style={{ backgroundColor: 'white', border: 'none', width: '335px', height: '40px', marginLeft: '7px', borderRadius: '5px' }} />
        </Form.Group>
        <Button variant="primary" type="submit" className="w-100 mb-4" style={{ backgroundColor: 'rgb(201, 58, 42)', border: 'none', borderRadius: '5px' }}>
          LOGIN
        </Button>

        <Button onClick={handleSignUpClick} className="btn btn-outline-primary w-100" style={{ backgroundColor: 'rgb(201, 58, 42)', border: 'none', borderRadius: '5px' }}>
          SIGN UP
        </Button>
      </Form>
    </Col>
    <Col xs={12} md={6} className="d-none d-md-block position-relative">
      <Image src={loginImage} alt="Login Image" fluid className="position-absolute top-50 start-50 translate-middle" style={{ maxWidth: '30%', maxHeight: '30%', transform: 'translate(-50%, -50%)', borderRadius: '5px' }} />
    </Col>
  </Row>
</Container>

  
  );
};

export default Login;
