import React, { useState } from 'react';
import axios from 'axios';
import { Button, Modal, Container, Form, Image, Stack} from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import ReCAPTCHA from 'react-google-recaptcha';
import meloMixLogo from './meloMixLogo.png';
import 'bootstrap/dist/css/bootstrap.min.css';

const UserTypeSelection = () => {
  const navigate = useNavigate();
  const { userType } = useParams();
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    username: '',
    email: '',
    password: '',
    password_confirmation: '',
    profile_picture: null
  });
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [captchaToken, setCaptchaToken] = useState('');

  const handleCloseModal = () => setShowModal(false);
  

  const selectUserType = (userType) => {
    if (userType === 'artist') {
      alert("Artists can't register yet");
    } else {
      setShowModal(true); 
    }
  };

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = e => {
    setFormData({ ...formData, profile_picture: e.target.files[0] });
  };
  const handleCancel = () => {
    navigate('/login');
    setShowModal(false);
  };
  const handleSubmit = async e => {
    e.preventDefault();
    if (!captchaToken) {
      setMessage('Please complete the CAPTCHA.');
      return;
    }
    setIsLoading(true);
    try {
      const data = new FormData();
      for (let key in formData) {
        data.append(key, formData[key]);
      }
      const response = await axios.post(`http://localhost:8000/api/register/${userType}`, data);
      setMessage('Registration successful!');
    } catch (error) {
      if (error.response && error.response.status === 422) {
        const validationErrors = error.response.data.errors;
        console.log(validationErrors);
      } else {
        setMessage('Registration failed. Please try again.');
        console.error('Registration failed:', error);
      }
    } finally {
      setIsLoading(false);
      setFormData({
        first_name: '',
        last_name: '',
        username: '',
        email: '',
        password: '',
        password_confirmation: '',
        profile_picture: null
      });
      handleCloseModal(); // Close modal after submission
    }
  };

  const handleCaptchaChange = token => {
    setCaptchaToken(token);
  };

  return ( 
    <div style={{ backgroundColor: '#800000', padding: '20px' }}>
      <img src={meloMixLogo} alt="MeloMix Logo" />
      <h1 style={{ color: 'white' }}>MeloMix</h1>
      <h5 style={{ color: 'white' }}>Sign Up as:</h5>
      <Button variant="light" onClick={() => selectUserType('listener')}>A Listener</Button>{' '}
      <Button variant="light" onClick={() => selectUserType('artist')}>An Artist</Button>

      {/* Modal */}
      <Modal show={showModal} aria-labelledby="contained-modal-title-vcenter"centered  className="backdrop">
      <Modal.Body style={{ backgroundColor: '#2D2D2D', borderRadius: '5px'}}>
        <Container>
          {message && <p>{message}</p>}
          <Form onSubmit={handleSubmit} className="d-flex flex-column align-items-center">
          <div className="file-input-container mb-3" style={{ backgroundColor: '#fff' }}>
              <label htmlFor="profile_picture" className="upload-button">
                {formData.profile_picture ? (
                  <Image src={URL.createObjectURL(formData.profile_picture)} alt="Profile" className="profile-image" />
                ) : (
                  <span>Upload Image</span>
                )}
              </label>
              <Form.Control type="file" name="profile_picture" id="profile_picture" onChange={handleFileChange} />
            </div>
            <Stack gap={2} className='mb-4 mx-auto' style={{width: '90%'}}>
              <Form.Control type="text" name="first_name" placeholder="First Name" value={formData.first_name} onChange={handleChange} style={{textAlign:'center'}} />
              <Form.Control type="text" name="last_name" placeholder="Last Name" value={formData.last_name} onChange={handleChange} style={{textAlign:'center'}} />
              <Form.Control type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} style={{textAlign:'center'}}/>
              <Form.Control type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} style={{textAlign:'center'}}/>
              <Form.Control type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} style={{textAlign:'center'}}/>
              <Form.Control type="password" name="password_confirmation" placeholder="Confirm Password" value={formData.password_confirmation} onChange={handleChange} style={{textAlign:'center'}}/>
            </Stack>
            
            <ReCAPTCHA
              className='mb-4'
              sitekey="6Ldldm4pAAAAAKY_GR9PwuY1FG07D3hRNiwG1o8i"
              onChange={handleCaptchaChange}
            />
            </Form>
            <div className="d-grid gap-2 d-flex flex-column align-items-center mb-3"  >
              <Button variant="danger" className="w-50" size="sm" type="submit" disabled={isLoading}>
                SIGN UP
              </Button>
              <Button variant="secondary"  className="w-50" size="sm" onClick={handleCancel}>
                CANCEL
              </Button>
            </div>
        </Container>
      </Modal.Body>
    </Modal>
      
    </div>
  );
};

export default UserTypeSelection;
