import React, { useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import ReCAPTCHA from 'react-google-recaptcha';
import { Modal, Container, Form, Button, Image } from 'react-bootstrap';
import meloMixLogo from './meloMixLogo.png';
import './SignUp.css';

const SignUpModal = () => {
  const navigate = useNavigate();
  const { userType } = useParams();
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    username: '',
    email: '',
    password: '',
    password_confirmation: '',
    profile_picture: null
  });
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [captchaToken, setCaptchaToken] = useState('');
  const [show, setShow] = useState(true);

  const handleClose = () => setShow(false);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = e => {
    setFormData({ ...formData, profile_picture: e.target.files[0] });
  };

  const handleCancel = () => {
    navigate('/login');
    setShow(false);
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!captchaToken) {
      setMessage('Please complete the CAPTCHA.');
      return;
    }
    setIsLoading(true);
    try {
      const data = new FormData();
      for (let key in formData) {
        data.append(key, formData[key]);
      }
      const response = await axios.post(`http://localhost:8000/api/register/${userType}`, data);
      setMessage('Registration successful!');
    } catch (error) {
      if (error.response && error.response.status === 422) {
        const validationErrors = error.response.data.errors;
        console.log(validationErrors);
      } else {
        setMessage('Registration failed. Please try again.');
        console.error('Registration failed:', error);
      }
    } finally {
      setIsLoading(false);
      setFormData({
        first_name: '',
        last_name: '',
        username: '',
        email: '',
        password: '',
        password_confirmation: '',
        profile_picture: null
      });
    }
  };

  const handleCaptchaChange = token => {
    setCaptchaToken(token);
  };

  return (
    <Modal show={show} onHide={handleClose} centered>
      <Modal.Body>
        <Container>
          <Image src={meloMixLogo} alt="MeloMix Logo" />
          <h1>MeloMix</h1>
          {message && <p>{message}</p>}
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="first_name">
              <Form.Label>First Name</Form.Label>
              <Form.Control type="text" name="first_name" value={formData.first_name} onChange={handleChange} />
            </Form.Group>
            <Form.Group controlId="last_name">
              <Form.Label>Last Name</Form.Label>
              <Form.Control type="text" name="last_name" value={formData.last_name} onChange={handleChange} />
            </Form.Group>
            <Form.Group controlId="username">
              <Form.Label>Username</Form.Label>
              <Form.Control type="text" name="username" value={formData.username} onChange={handleChange} />
            </Form.Group>
            <Form.Group controlId="email">
              <Form.Label>Email</Form.Label>
              <Form.Control type="email" name="email" value={formData.email} onChange={handleChange} />
            </Form.Group>
            <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" name="password" value={formData.password} onChange={handleChange} />
            </Form.Group>
            <Form.Group controlId="password_confirmation">
              <Form.Label>Confirm Password</Form.Label>
              <Form.Control type="password" name="password_confirmation" value={formData.password_confirmation} onChange={handleChange} />
            </Form.Group>
            <Form.Group controlId="profile_picture">
              <Form.File id="profile_picture" label="Upload Image" onChange={handleFileChange} />
            </Form.Group>
            <ReCAPTCHA
              sitekey="6Ldldm4pAAAAAKY_GR9PwuY1FG07D3hRNiwG1o8i"
              onChange={handleCaptchaChange}
            />
            <Button variant="primary" type="submit" disabled={isLoading}>
              Sign Up
            </Button>
            <Button variant="secondary" onClick={handleCancel}>
              Cancel
            </Button>
          </Form>
        </Container>
      </Modal.Body>
    </Modal>
  );
};

export default SignUpModal;
